mapper.select:
SELECT r.*, b.building_name FROM t_dorm_room r INNER JOIN t_dorm_building b ON r.building_id = b.building_id
AND r.building_id = #{buildingId}
AND r.room_number LIKE CONCAT('%', #{roomNumber}, '%')
AND r.room_status = #{roomStatus}
ORDER BY r.room_id

mapper.update:
UPDATE t_dorm_room
building_id = #{buildingId}, room_number = #{roomNumber}, full_name = #{fullName}, total_beds = #{totalBeds}, occupied_beds = #{occupiedBeds}, room_status = #{roomStatus},
WHERE room_id = #{roomId}
