mapper.update:
UPDATE t_dorm_room
building_id = #{buildingId}, room_number = #{roomNumber}, full_name = #{fullName}, total_beds = #{totalBeds}, occupied_beds = #{occupiedBeds}, room_status = #{roomStatus}, room_type = #{roomType}, has_private_bathroom = #{hasPrivateBathroom},
WHERE room_id = #{roomId}

mapper.select:
SELECT b.room_id, b.bed_id, b.bed_number, u.user_id AS student_id, u.full_name, u.phone_number FROM t_dorm_bed b LEFT JOIN t_user u ON b.current_resident_id = u.user_id WHERE b.room_id IN
#{roomId}
AND b.bed_status = 2 ORDER BY b.room_id, b.bed_number
