mapper.select:
SELECT hs.*, u.full_name as student_name, u.student_id_number
FROM t_homework_submission hs
INNER JOIN t_user u ON hs.student_id = u.user_id
WHERE hs.homework_id = #{homeworkId}

    AND hs.status = #{status}

ORDER BY hs.submitted_at DESC

SELECT * FROM t_homework_submission 
WHERE student_id = #{studentId}
ORDER BY submitted_at DESC

mapper.insert:
INSERT INTO t_homework_submission (homework_id, student_id, content, submitted_at, status)
VALUES (#{homeworkId}, #{studentId}, #{content}, NOW(), #{status})
ON DUPLICATE KEY UPDATE content = #{content}, submitted_at = NOW(), status = #{status}

mapper.update:
UPDATE t_homework_submission
content = #{content}, status = #{status}, grade = #{grade}, teacher_feedback = #{teacherFeedback},
WHERE submission_id = #{submissionId}
